	/**
     * Funcion userByDevice busca y lista todos los usuarios
     * por dispositivo.
     *
     * @param  number  IP
     * @return json  respuesta
     */
    public function userByDevice(Request $request){
        //Validacion de campos que se pueden recibir
        $this->validate($request, [
            'IP' => 'required',
            'Type' => 'required'
        ]);

        try {

            $cmd = new BioTrackCmd (request('Type'), request('IP'));
            

	        if (!$cmd->get_users()) {
	        	return response()->json(['respuesta'=> 'No hay usuarios en este dispositivo.'],422);
	        }else{

                $cmdResponse = $cmd->get_users();
               
                $ids = [];
                foreach ($cmd->get_users() as $user)
                {
                   array_push($ids, $user['userId']);
                }

                $sorted =  DB::table('User')
                            ->join('Department', 'User.IdDepartment', '=', 'Department.IdDepartment')
                            ->whereIn('IdUser', $ids)
                            ->orderBy('User.Name', 'asc')
                            ->get();

                return response()->json(['respuesta'=> $sorted],200);
            }

        } catch (Exception $e) {
        	return response()->json(['respuesta'=> 'Error inesperado userByDevice BioTrackCmdController.'],422);
        }  
    }

    /**
     * Funcion inject_user_to_device inyecta directamente al dispositivo un 
     * nuevo usuario. El parametro $data contiene las huellas, rostro del empleado
     * y otros datos necesarios.
     * Ejemplo:
     * $data = [
     *       'userId' => $id,
     *       'userName' => $name,
     *       'userPassword' => '123456',
     *       'userPrivilege'=> 0,
     *       'userCardNumber'=> '',
     *       'userEnabled'=> true,
     *       'userFingerprints'=> [
     *           $fingerprint[0],
     *           $fingerprint[1],
     *           $fingerprint[2],
     *           $fingerprint[3],
     *           $fingerprint[4],
     *           $fingerprint[5],
     *           $fingerprint[6],
     *           $fingerprint[7],
     *           $fingerprint[8],
     *           $fingerprint[9]
     *       ],
     *       'userFace'=> ''
     *  ];
     *
     * NOTA: esta funcion es llamada en el controlador RegisterUSerController.php
     *
     * @param  string  ip_device
     * @param  array  data
     * @return boolean  true
     */
    public function inject_user_to_device($type, $ip_device, $data)
    {
        $cmd = new BioTrackCmd ($type, $ip_device);
        return $cmd->update_user_data($data);
    }

    /**
     * Funcion add_user inyecta directamente al dispositivo un 
     * nuevo usuario sin huellas, ni rostro.
