     * @param  string  user_id
     * @param  string  name
     * @return boolean  true
     */
    public function add_user($type, $ip_device, $user_id, $name)
    {
        $cmd = new BioTrackCmd ($type, $ip_device);
        return $cmd->add_user($user_id, '123456', $name);
    }

