    /**
     * Funcion data genera un estructura json necesaria
     * para registrar un empleado hacia el dispositivo fisico
     *
     * @param  int  id
     * @param  string  name
     * @param  string fingerprint
     * @return json  data
     */
    public function data($id, $name, $fingerprint)
    {
        $data = [
            'userId' => $id,
            'userName' => $name,
            'userPassword' => '123456',
            'userPrivilege'=> 0,
            'userCardNumber'=> '',
            'userEnabled'=> true,
            'userFingerprints'=> [
                $fingerprint[0],
                $fingerprint[1],
                $fingerprint[2],
                $fingerprint[3],
                $fingerprint[4],
                $fingerprint[5],
                $fingerprint[6],
                $fingerprint[7],
                $fingerprint[8],
                $fingerprint[9]
            ],
            'userFace'=> ''
        ];

        return $data;
    }

