    /**
     * Funcion open_door abre puerta de dispositivo enlazado.
     *
     * @param  string  IP
     * @return json  respuesta
     */
    public function open_door(Request $request)
    {
        //Validacion de campos que se pueden recibir
        $this->validate($request, [
            'IP' => 'required',
            'Type' => 'required'
        ]);

        $cmd = new BioTrackCmd (request('Type'), request('IP'));
        $open_door = $cmd->open_door();
        if ($open_door) {
            return response()->json(['respuesta1'=> "Bienvenido."],200);
        }else
            return response()->json(['respuesta2'=> "Intente de nuevo."],200);
    }

