            'Type' => 'required'
        ]);
        $cmd = new BioTrackCmd (request('Type'), request('IP'));
        $IdUser = request('IdUser');

        /*NOTA:
            para insertar una huella debe ser el dedo correcto el dispositivo
            ni la funcion notifican de que esta ingresando una huella correcta pero del dedo incorrecto*/
        $fingerprints = [
                                '',
                                '',
                                '',
                                '',
                                '',
                                '',
                                '',
                                '',
                                '',
                                ''];

        $insertarhuella = $cmd->update_user_fingerprint($IdUser, 5, $fingerprints);

        return response()->json(['respuesta'=> $insertarhuella],200);
    }
